import Header from './Header'
import React, {useState, useEffect} from "react"
import { useHistory } from 'react-router-dom'

function Login()
{
    const [key, setKey] = useState("")
    const [userPassword, setPassword] = useState("")
    const history = useHistory();

    useEffect(()=>{
        if(localStorage.getItem("user-info")) {
            history.push('/home');
        }
    }, []);

    return (
        <>
        <Header/>
        <div>
            <h1>Login Page</h1>
            <input type="text" value={userPassword} onChange={(e)=>setPassword(e.target.value)}  placeholder="User Name / Email" /> <br/><br/>
            <input type="password" value={userPassword} onChange={(e)=>setPassword(e.target.value)}  placeholder="Password" /> <br/><br/>
            <button className="btn btn-primary">Sign In</button> <br/><br/>
        </div>
        </>
    )
}

export default Login
