import {Navbar, Nav, Container, NavLink} from 'react-bootstrap'
import * as Icon from 'react-bootstrap-icons'
import { Component } from 'react'
import $ from 'jquery'

class Header extends Component {
    componentDidMount() {
        $(document).ready(function() {
            var activetab = $("Nav.Link active");
            console.log(activetab);
        });
    }

    render()
    {
        return (
            <div>
                <Navbar variant="light" expand="xl" style={{ backgroundColor:"#E2EFFF" }}>
                    <Nav className="d-flex justify-content-between mx-3">
                        <NavLink href="/home" id="navTrainzilla">TRAINZILLA</NavLink>
                    </Nav>
                    <Container>
                        <Navbar.Toggle aria-controls="basic-navbar-nav" />
                        <Navbar.Collapse id="basic-navbar-nav">
                            <Nav className="m-auto d-flex justify-content-between">
                                <NavLink href="/home" className="mx-5" style={isActive => ({ backgroundColor: isActive ? "red" : "" })} >HOME</NavLink>
                                <NavLink href="/maproute" className="mx-5">MAP & ROUTE</NavLink>
                                <NavLink href="/announcement" className="mx-5">ANNOUNCEMENT</NavLink>
                                <NavLink href="/aboutus" className="mx-5">ABOUT US</NavLink>
                            </Nav>
                            <Nav className="d-flex flex-row justify-content-center mx-3">
                                <NavLink href="/profile"><Icon.PersonFill size={24}/></NavLink>
                                <NavLink href="/login">LOGIN</NavLink>
                            </Nav>
                        </Navbar.Collapse>
                    </Container>
                </Navbar>
            </div>
        )
    }
}

export default Header
